import{_ as i,c as t,a2 as a,o}from"./chunks/framework.DYF6f1aH.js";const u=JSON.parse('{"title":"Quick Start","description":"","frontmatter":{"layout":"home","hero":{"name":"micromissiles-unity","tagline":"Swarm-on-swarm simulator using micromissiles for point defense","actions":[{"theme":"brand","text":"Documentation","link":"/Simulator_Overview"},{"theme":"alt","text":"Development Guide","link":"/Development_Guide"}]}},"headers":[],"relativePath":"index.md","filePath":"index.md"}'),n={name:"index.md"};function r(s,e,l,d,c,h){return o(),t("div",null,e[0]||(e[0]=[a('<h1 id="quick-start" tabindex="-1">Quick Start <a class="header-anchor" href="#quick-start" aria-label="Permalink to &quot;Quick Start&quot;">​</a></h1><p>We generate pre-built standalone binaries for Windows and Mac users from the <code>release</code> branch. These binaries are intended for non-development users who just want to run the application and modify a few configurations along the way.</p><p>You can find the latest release <a href="https://github.com/PisterLab/micromissiles-unity/releases/latest" target="_blank" rel="noreferrer">here</a>.</p><p>Follow the instructions below for your operating system to download and run the application.</p><h3 id="windows" tabindex="-1">Windows <a class="header-anchor" href="#windows" aria-label="Permalink to &quot;Windows&quot;">​</a></h3><ol><li>Download the zip file for Windows: <code>micromissiles-&lt;version&gt;-windows_x86_64.zip</code>.</li><li>Unzip the zip file. The zip file should contain a single directory called <code>micromissiles-&lt;version&gt;-windows_x86_64</code>.</li><li>In the <code>micromissiles-&lt;version&gt;-windows_x86_64</code> directory, run <code>micromissiles-&lt;version&gt;-StandaloneWindows64.exe</code>.</li></ol><h3 id="mac" tabindex="-1">Mac <a class="header-anchor" href="#mac" aria-label="Permalink to &quot;Mac&quot;">​</a></h3><ol><li>Download the tarball file for Darwin: <code>micromissiles-&lt;version&gt;-darwin.tar.gz</code>.</li><li>Untar the tarball. The tarball should contain a single directory called <code>micromissiles-&lt;version&gt;-darwin</code>.</li><li>In the <code>micromissiles-&lt;version&gt;-darwin</code> directory, run the app file.</li></ol><h2 id="basics" tabindex="-1">Basics <a class="header-anchor" href="#basics" aria-label="Permalink to &quot;Basics&quot;">​</a></h2><ul><li>Use the mouse and arrow keys (or WASD) to pan and rotate the camera.</li><li>Use <code>L</code> to load a new simulation configuration.</li><li>Use <code>R</code> to restart the simulation.</li><li>Use <code>SPACEBAR</code> to pause/resume the simulation.</li><li>Use <code>ESC</code> to exit the application.</li></ul><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><ul><li>Read the <a href="./Simulator_Overview.html"><strong>Simulation Overview</strong></a> to understand the simulation physics fidelity, agent behaviors, and assumptions.</li><li>Familiarize yourself with the <a href="./Keybinds_and_Controls.html"><strong>Keybinds and Controls</strong></a> to navigate and interact with the simulation.</li><li>Learn how to configure the simulation settings by reading the <a href="./Simulation_Config_Guide.html"><strong>Simulation Configuration Guide</strong></a>.</li></ul>',12)]))}const p=i(n,[["render",r]]);export{u as __pageData,p as default};
